def get_total_cards():
    """–ü–æ–ª—É—á–∏—Ç—å –æ–±—â–µ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞—Ä—Ç –≤ –∫–æ–ª–æ–¥–µ"""
    return len(tarot_deck)

def get_cards_by_type():
    """–ü–æ–ª—É—á–∏—Ç—å —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É –∫–∞—Ä—Ç –ø–æ —Ç–∏–ø–∞–º"""
    major_arcana = [card for card in tarot_deck if not any(suit in card['name'] for suit in ['–ö—É–±–∫–æ–≤', '–ü–µ–Ω—Ç–∞–∫–ª–µ–π', '–ú–µ—á–µ–π', '–ñ–µ–∑–ª–æ–≤'])]
    minor_arcana = [card for card in tarot_deck if any(suit in card['name'] for suit in ['–ö—É–±–∫–æ–≤', '–ü–µ–Ω—Ç–∞–∫–ª–µ–π', '–ú–µ—á–µ–π', '–ñ–µ–∑–ª–æ–≤'])]
    
    return {
        'major_arcana': len(major_arcana),
        'minor_arcana': len(minor_arcana),
        'total': len(tarot_deck)
    }
